sys_ux_client_script.script:
function evaluateProperty({api}) {
	if(api.data.get_article_details_1.articleDetails){
		var articleData = api.data.get_article_details_1.articleDetails;
		if(articleData.versionDetails.isEnabled && articleData.versionDetails.history.length>1){
			return false;
		}
	}
	return 'true';
}
