sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.imports
 */
function evaluateProperty({
    api
}) {
	if (!api.data.get_article_details_1.articleDetails || !api.data.get_article_details_1.articleDetails.versionInfo)
		return;

	let versionInformation = api.data.get_article_details_1.articleDetails.versionInfo;
    let versions = versionInformation.versions;
    let selectedVersionId = versionInformation.selectedVersion;
    let selectedVersion = versions.find(version => version.id == selectedVersionId);
    let value = '';

	if(selectedVersion)
		 value = "Version " + selectedVersion.label + ", Select to choose other available versions of the article";

    return {
        "aria-label": value
    };
}
